import './App.css';
import { useEffect, useState } from 'react';


window.addEventListener("click", (event) => 
{
  if (event.button === 0) 
  {
    ShootBall(event.clientX, event.clientY);
  }
});
function ShootBall(xPos, yPos) 
{
  MoveBall(xPos, yPos, 6, document.getElementsByClassName("ball")[0].getBoundingClientRect(), document.styleSheets[0].cssRules[3].style);
  console.log("Shot ball");
}

function EndGame() 
{
  document.styleSheets[0].cssRules[1].style.left = "2000px";
  document.styleSheets[0].cssRules[1].style.top = "200px";
}

function StartGame() 
{
  console.log("here");
  setTimeout(function () 
  {
    MoveObstacle("obstacle", 1);
  }, 3000);
}

function MoveBall(xPos, yPos, speed, ball, style) 
{
  if (ball.left < xPos) 
  {
    style.left = (parseInt(style.left.substring(0, style.left.length - 2)) + speed) + "px";
  }
  else if (Math.round(ball.left / 10) > Math.round(xPos / 10)) 
  {
    style.left = (parseInt(style.left.substring(0, style.left.length - 2)) - speed) + "px";
  }
  if (ball.top < yPos) 
  {
    style.top = (parseInt(style.top.substring(0, style.top.length - 2)) + speed) + "px";
  }
  else if (Math.round(ball.top / 10) > Math.round(yPos / 10)) 
  {
    style.top = (parseInt(style.top.substring(0, style.top.length - 2)) - speed) + "px";
  }
  if (style.left < 1000 && style.left > -1000 && style.top < 2000 && style.top > -1000) 
  {
    MoveBall(xPos, yPos, speed, ball, style);
  }
  else 
  {
    console.log(style.left + " " + style.top + " " + xPos + " " + yPOs);
  }
}

function MoveObstacle(className, ruleId) 
{
    let elementStyle = document.styleSheets[0].cssRules[ruleId].style;
      setTimeout(function () 
      {
          const rect1 = document.getElementsByClassName(className)[0].getBoundingClientRect();
          const rect2 = document.getElementsByClassName("dot")[0].getBoundingClientRect();
          if (rect1.bottom > rect2.bottom) 
          {
            elementStyle.top = (parseInt(elementStyle.top.substring(0, elementStyle.top.length - 2)) - 6) + "px";
          }
          else 
          {
            console.log(rect1.bottom + " " + rect2.bottom);
          }
          if (rect1.left > rect2.left) 
          {
            elementStyle.left = (parseInt(elementStyle.left.substring(0, elementStyle.left.length - 2)) - 6) + "px";
          }
          if (Math.round(rect1.bottom / 10) < Math.round((rect2.bottom / 10))) 
          {
            console.log("1 " +elementStyle.top);
            elementStyle.top = (parseInt(elementStyle.top.substring(0, elementStyle.top.length - 2)) + 6) + "px";
            console.log("2 "+ elementStyle.top);
          }
          console.log(rect1.left);
          console.log(rect2.left);
          if ((rect1.left < rect2.right && rect1.right > rect2.left) && ((rect1.top <= rect2.top && (rect1.bottom > rect2.top + 10)) || (rect1.top >= rect2.top && rect1.top < (rect2.bottom - 10)))) 
          { 
            EndGame();
          }
          else 
          {
            MoveObstacle(className, ruleId);
          }
      }, 15);
}

function App() 
{

  const [counter, setCounter] = useState(parseInt(localStorage.getItem('savedCounter')));

  useEffect(() => 
  {
    localStorage.setItem('savedCounter', counter);
  }, [counter])
//  <h1>Counter App</h1>
//  <button className = 'buttonItems' onClick = {() => setCounter(counter - 1)}> - </button>
//  <h2 className = 'buttonItems'>{counter}</h2>
//  <button className = 'buttonItems' onClick = {() => setCounter(counter + 1)}> + </button>
//  <button onClick = {() => setCounter(0)}>Reset</button> 
return (
    <div>
      <button className = 'buttonItems' onClick = {() => StartGame()}>Play</button>
      <div className = 'dot'></div>
      <div className = 'obstacle'></div>
      <div className = 'ball'></div>

    </div>
  );
}

export default App;
