import './App.css';
import { useEffect, useState } from 'react';

let play = false;
let movingBall = false;
let id = 0;
const defaultXPos = 750;
const defaultYPos = 300;

window.addEventListener("click", (event) => 
{
  if (play && event.button === 0) 
  {
    if (movingBall === true) 
    {
      StopTimeouts(false);
      ReturnToSender();
    }
    else 
    {
      ShootBall(event.clientX, event.clientY);
    }
  }
});

function StopTimeouts(allTimeouts) 
{
    if (allTimeouts) 
    {
        let _id = window.setTimeout(function() {}, 0);

        while (_id--) 
        {
            window.clearTimeout(_id);
        }
    }
    else 
    {
        window.clearTimeout(id);
    }

}

function ShootBall(xPos, yPos) 
{
  movingBall = true;
  let style = document.styleSheets[0].cssRules[3].style;
  let horizontalSpeed = parseInt((xPos - defaultXPos) / 4);
  let verticalSpeed = parseInt((yPos - defaultYPos) / 4);
  while (Math.abs(horizontalSpeed) > 4 || Math.abs(verticalSpeed) > 4) 
  {
    horizontalSpeed = Math.round(horizontalSpeed / 1.5);
    verticalSpeed = Math.round(verticalSpeed / 1.5);
  }
  console.log("Speed: " + horizontalSpeed + " " + verticalSpeed);
  MoveBall(horizontalSpeed, verticalSpeed, style);
  console.log("Shot ball");
}

function EndGame() 
{
  play = false;
  document.styleSheets[0].cssRules[1].style.left = "2000px";
  document.styleSheets[0].cssRules[1].style.top = "200px";
}
function ReturnToSender() 
{
  setTimeout(function() 
  {
    if (movingBall === true) 
    {
      StopTimeouts(false);
      movingBall = false;
      document.styleSheets[0].cssRules[3].style.left = defaultXPos.toString() + "px";
      document.styleSheets[0].cssRules[3].style.top = defaultYPos.toString() + "px";
    }
  }, 50);
}

function StartGame() 
{
  console.log("here");
  setTimeout(function () 
  {
    play = true;
    MoveObstacle("obstacle", 1);
  }, 3000);
}

function MoveBall(horizontalSpeed, verticalSpeed, style) 
{
  id = setTimeout(function() 
  {
    style.left = (parseInt(style.left.substring(0, style.left.length - 2)) + horizontalSpeed) + "px";
    style.top = (parseInt(style.top.substring(0, style.top.length - 2)) + verticalSpeed) + "px";
    MoveBall(horizontalSpeed, verticalSpeed, style);
  }, 15);
}

function MoveObstacle(className, ruleId) 
{
    let elementStyle = document.styleSheets[0].cssRules[ruleId].style;
      setTimeout(function () 
      {
          const rect1 = document.getElementsByClassName(className)[0].getBoundingClientRect();
          const rect2 = document.getElementsByClassName("dot")[0].getBoundingClientRect();
          const rect3 = document.getElementsByClassName("ball")[0].getBoundingClientRect();
          if (rect1.bottom > rect2.bottom) 
          {
            elementStyle.top = (parseInt(elementStyle.top.substring(0, elementStyle.top.length - 2)) - 6) + "px";
          }
          else 
          {
            console.log(rect1.bottom + " " + rect2.bottom);
          }
          if (rect1.left > rect2.left) 
          {
            elementStyle.left = (parseInt(elementStyle.left.substring(0, elementStyle.left.length - 2)) - 6) + "px";
          }
          if (Math.round(rect1.bottom / 10) < Math.round((rect2.bottom / 10))) 
          {
            console.log("1 " +elementStyle.top);
            elementStyle.top = (parseInt(elementStyle.top.substring(0, elementStyle.top.length - 2)) + 6) + "px";
            console.log("2 "+ elementStyle.top);
          }
          console.log(rect1.left);
          console.log(rect2.left);
          if ((rect1.left < rect3.right && rect1.right > rect3.left) && ((rect1.top <= rect3.top && (rect1.bottom > rect3.top + 10)) || (rect1.top >= rect3.top && rect1.top < (rect3.bottom - 10)))) 
          { 
            ReturnToSender();
            EndGame();
          }
          else if ((rect1.left < rect2.right && rect1.right > rect2.left) && ((rect1.top <= rect2.top && (rect1.bottom > rect2.top + 10)) || (rect1.top >= rect2.top && rect1.top < (rect2.bottom - 10)))) 
          { 
            EndGame();
          }
          else 
          {
            MoveObstacle(className, ruleId);
          }
      }, 15);
}

function App() 
{

  const [counter, setCounter] = useState(parseInt(localStorage.getItem('savedCounter')));

  useEffect(() => 
  {
    localStorage.setItem('savedCounter', counter);
  }, [counter])
//  <h1>Counter App</h1>
//  <button className = 'buttonItems' onClick = {() => setCounter(counter - 1)}> - </button>
//  <h2 className = 'buttonItems'>{counter}</h2>
//  <button className = 'buttonItems' onClick = {() => setCounter(counter + 1)}> + </button>
//  <button onClick = {() => setCounter(0)}>Reset</button> 
return (
    <div>
      <button className = 'buttonItems' onClick = {() => StartGame()}>Play</button>
      <div className = 'dot'></div>
      <div className = 'obstacle'></div>
      <div className = 'ball'></div>

    </div>
  );
}

export default App;
