import './App.css';
import { useEffect, useState } from 'react';

function DoNothing() 
{
  console.log("nothing");
}
function ResetGame() 
{
  console.log("here");
  let elementRules = document.styleSheets[0].cssRules;
  setTimeout(function () 
  {
    MoveObstacle("obstacle", 1);
  }, 3000);
}

function MoveObstacle(className, ruleId) 
{
    let elementStyle = document.styleSheets[0].cssRules[ruleId].style;
      setTimeout(function () 
      {
          if (elementStyle.bottom > document.styleSheets[0].cssRules[0].style.bottom) 
          {
            elementStyle.bottom = (parseInt(elementStyle.bottom.substring(0, elementStyle.bottom.length - 2)) - 3) + "px";
          }
          if (elementStyle.left > document.styleSheets[0].cssRules[0].style.left) 
          {
            elementStyle.left = (parseInt(elementStyle.left.substring(0, elementStyle.left.length - 2)) - 3) + "px";
          }
          if (elementStyle.top < document.styleSheets[0].cssRules[0].style.top) 
          {
            elementStyle.top = (parseInt(elementStyle.top.substring(0, elementStyle.top.length - 2)) - 3) + "px";
          }
          MoveObstacle(className, ruleId);
          const rect1 = document.getElementsByClassName(className)[0].getBoundingClientRect();
          const rect2 = document.getElementsByClassName("dot")[0].getBoundingClientRect();
          if ((rect1.left < rect2.right && rect1.right > rect2.left) && ((rect1.top <= rect2.top && (rect1.bottom > rect2.top + 10)) || (rect1.top >= rect2.top && rect1.top < (rect2.bottom - 10)))) ResetGame();
      }, 15);
}

function App() 
{

  const [counter, setCounter] = useState(parseInt(localStorage.getItem('savedCounter')));

  useEffect(() => 
  {
    localStorage.setItem('savedCounter', counter);
  }, [counter])
